<script>
export default {
  name: "MobileNav",
  data() {
    return {
      spanClass: "",
      show: false,
      bgColor: "",
      categories: ['covid', 'entertainment', 'news', 'politics', 'opinion', 'science', 'technology', 'sports'],
    }
  },
  computed: {
    backgroundColor: function () {
      return "background-color: " + this.bgColor;
    },
  },
  methods: {
    changeMenu: function() {
      if (this.spanClass != "open") {
        this.spanClass = "open";
      } else {
        this.spanClass = "";
      }
    },
    toggleNav: function() {
      this.show = !this.show;
    },
    toggleBackground: function() {
      if (this.bgColor === "") {
        this.bgColor = "var(--primary-color)";
      } else {
        this.bgColor = "";
      }
    },
    navFunction: function() {
      this.toggleBackground();
      this.toggleNav();
      this.changeMenu();
    }
  }
}
</script>

